function event_allowed(event_type)
{
    return flow.get('allowed-events').includes(event_type)
}
function disallow_event(event_type)
{
    var events = flow.get('allowed-events');
    var index = events.indexOf(event_type);
    if (index !== -1) {
        events.splice(index, 1);
        flow.set('allowed-events', events)
    }
}
